mapper.select:
SELECT * FROM admin
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_login_name = #{adminLoginName}

mapper.insert:
INSERT INTO `admin` (
    `gmt_created`
    , `gmt_modified`
    , `admin_login_name`
    , `admin_login_pass`
    , `admin_role_code`
    , `org_name`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{adminLoginName}
    , #{adminLoginPass}
    , #{adminRoleCode}
    , #{orgName}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE admin SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_login_name = #{adminLoginName}

mapper.delete:
DELETE FROM admin
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_login_name = #{adminLoginName}
