mapper.select:
SELECT * FROM admin_role
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_role_code = #{adminRoleCode}

mapper.insert:
INSERT INTO `admin_role` (
    `gmt_created`
    , `gmt_modified`
    , `admin_role_code`
    , `admin_role_name`
    , `tenant_code`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{adminRoleCode}
    , #{adminRoleName}
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE admin_role SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_role_code = #{adminRoleCode}

mapper.delete:
DELETE FROM admin_role
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_role_code = #{adminRoleCode}
