mapper.select:
SELECT * FROM admin_role_permit_act_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}
    AND role_code = #{roleCode}
    AND permit_act_code = #{permitActCode}

SELECT * FROM admin_role_permit_act_rel
WHERE 1 = 1
    AND tenant_code = #{tenantCode}

SELECT * FROM admin_role_permit_act_rel
WHERE 1 = 1

        AND tenant_code = #{tenantCode}


        AND role_code = #{roleCode}


        AND permit_act_code = #{permitActCode}

mapper.insert:
INSERT INTO admin_role_permit_act_rel (
    `gmt_created`
    , `gmt_modified`
    , `role_code`
    , `permit_act_code`
    , `tenant_code`
) VALUES (
    now()
    , now()
    , #{roleCode}
    , #{permitActCode}
    , #{tenantCode}
)

mapper.delete:
DELETE FROM admin_role_permit_act_rel
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and role_code = #{roleCode}
