mapper.select:
SELECT * FROM clean_rule
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and clean_rule_code = #{cleanRuleCode}

mapper.insert:
INSERT INTO `clean_rule` (
    `gmt_created`
    , `gmt_modified`
    , `clean_rule_code`
    , `clean_rule_name`
    , `permit_remind`
    , `permit_batch`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{cleanRuleCode}
    , #{cleanRuleName}
    , #{permitRemind}
    , #{permitBatch}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE clean_rule SET
clean_rule_name = #{cleanRuleName}
, permit_remind = #{permitRemind}
, permit_batch = #{}
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_login_name = #{adminLoginName}

mapper.delete:
DELETE FROM admin
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_login_name = #{adminLoginName}
