mapper.select:
SELECT * FROM machine
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and machine_code = #{machineCode}

mapper.insert:
INSERT INTO `machine` (
    `gmt_created`
    , `gmt_modified`
    , `screen_code`
    , `elec_board_code`
    , `model_code`
    , `machine_name`
    , `state`
    , `valid_until`
    , `maintain_until`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{screenCode}
    , #{elecBoardCode}
    , #{modelCode}
    , #{machineName}
    , #{state}
    , #{validUntil}
    , #{maintainUntil}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE machine SET
    `screen_code` = #{screenCode}
    `elec_board_code` = #{elecBoardCode}
    `model_code` = #{modelCode}
    `machine_name` = #{machineName}
    `state` = #{state}
    `valid_until` = #{validUntil}
    `maintain_until` = #{maintainUntil}
    `tenant_code` = #{tenantCode}
    `comment` = #{comment}
    `extra_info` = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and machine_code = #{machineCode}

mapper.delete:
DELETE FROM machine
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and machine_code = #{machineCode}
