mapper.insert:
INSERT INTO `machine_model_pipeline` (
    `gmt_created`
    , `gmt_modified`
    , `model_code`
    , `pipeline_num`
    , `warm_support`
    , `freeze_support`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{modelCode}
    , #{pipelineNum}
    , #{warmSupport}
    , #{freezeSupport}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE machine_model_pipeline SET
     warm_support = #{warmSupport}
    , freeze_support = #{freezeSupport}
    , extra_info = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
WHERE model_code = #{modelCode}
    and pipeline_num = #{pipelineNum}
