mapper.select:
SELECT * FROM org_struc
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and orgName = #{orgName}

mapper.insert:
INSERT INTO `org_struc` (
    `gmt_created`
    , `gmt_modified`
    , `org_name`
    , `parent_org_name`
    , `tenant_code`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{orgName}
    , #{parentOrgName}
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE org_struc SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and org_name = #{orgName}

mapper.delete:
DELETE FROM org_struc
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and org_name = #{orgName}
