mapper.select:
SELECT * FROM role_perm_rel
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_role_code = #{adminRoleCode}
    and perm_act_code = #{adminRoleCode}

mapper.insert:
INSERT INTO `role_perm_rel` (
    `gmt_created`
    , `gmt_modified`
    , `admin_role_code`
    , `perm_act_code`
    , `tenant_code`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{adminRoleCode}
    , #{permActCode}
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE role_perm_rel SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_role_code = #{adminRoleCode}

mapper.delete:
DELETE FROM role_perm_rel
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and admin_role_code = #{adminRoleCode}
