mapper.select:
SELECT * FROM series
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and series_code = #{seriesCode}

mapper.insert:
INSERT INTO `series` (
    `gmt_created`
    , `gmt_modified`
    , `series_code`
    , `series_name`
    , `img_link`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{seriesCode}
    , #{seriesName}
    , #{imgLink}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE series SET
    series_name = #{seriesName}
    , img_link = #{imgLink}
    , tenant_code = #{tenantCode}
    , comment = #{comment}
    , extra_info = #{extraInfo}
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and series_code = #{seriesCode}

mapper.delete:
DELETE FROM admin
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and series_code = #{seriesCode}
