mapper.select:
SELECT * FROM shop_group
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_group_code = #{shopGroupCode}

mapper.insert:
INSERT INTO `shopGroupPOMap` (
    `gmt_created`
    , `gmt_modified`
    , `shop_group_code`
    , `shop_group_name`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{shopGroupCode}
    , #{shopGroupName}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE shop_group SET
    `shop_group_name` = #{shopName}
    `tenant_code` = #{tenantCode}
    `comment` = #{comment}
    `extra_info` = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_group_code = #{shopGroupCode}

mapper.delete:
DELETE FROM shop_group
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_group_code = #{shopGroupCode}
