mapper.select:
SELECT * FROM shop
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_code = #{shopCode}

mapper.insert:
INSERT INTO `shop` (
    `gmt_created`
    , `gmt_modified`
    , `shop_code`
    , `shop_name`
    , `shop_type`
    , `shop_group_code`
    , `org_name`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{shopCode}
    , #{shopName}
    , #{shopType}
    , #{shopGroupCode}
    , #{orgName}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE shop SET
    `shop_name` = #{shopName}
    `shop_type` = #{shopType}
    `shop_group_code` = #{shopGroupCode}
    `org_name` = #{orgName}
    `tenant_code` = #{tenantCode}
    `comment` = #{comment}
    `extra_info` = #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_code = #{shopCode}

mapper.delete:
DELETE FROM shop
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and shop_code = #{shopCode}
