mapper.select:
SELECT * FROM tea_spec
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_spec_code = #{toppingSpecCode}

mapper.insert:
INSERT INTO `tea_spec` (
    `gmt_created`
    , `gmt_modified`
    , `topping_spec_code`
    , `topping_spec_name`
    , `state`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{toppingSpecCode}
    , #{toppingSpecName}
    , #{state}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE tea_spec SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_spec_code = #{toppingSpecCode}

mapper.delete:
DELETE FROM tea_spec
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_spec_code = #{toppingSpecCode}
