mapper.select:
SELECT * FROM tea_spec_sub
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and spec_sub_code = #{specSubCode}

mapper.insert:
INSERT INTO `tea_spec` (
    `gmt_created`
    , `gmt_modified`
    , `spec_code`
    , `spec_sub_code`
    , `spec_sub_name`
    , `outer_spec_sub_code`
    , `tenant_code`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{specCode}
    , #{specSubCode}
    , #{specSubName}
    , #{outerSpecSubCode}
    , #{tenantCode}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE tea_spec SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and spec_sub_code = #{specSubCode}

mapper.delete:
DELETE FROM tea_spec
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and spec_sub_code = #{specSubCode}
