mapper.select:
SELECT * FROM tea_topping
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_code = #{toppingCode}

mapper.insert:
INSERT INTO `tea_topping` (
    `gmt_created`
    , `gmt_modified`
    , `topping_code`
    , `topping_name`
    , `topping_type_code`
    , `weight_unit`
    , `state`
    , `valid_hour_period`
    , `clean_hour_period`
    , `convert_coefficient`
    , `flow_speed`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{toppingCode}
    , #{toppingName}
    , #{toppingTypeCode}
    , #{weightUnit}
    , #{state}
    , #{validHourPeriod}
    , #{cleanHourPeriod}
    , #{convertCoefficient}
    , #{flowSpeed}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE tea_topping SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_code = #{toppingCode}

mapper.delete:
DELETE FROM tea_topping
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_code = #{toppingCode}
