mapper.select:
SELECT * FROM tea_topping_type
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_type_code = #{toppingTypeCode}

mapper.insert:
INSERT INTO `tea_topping_type` (
    `gmt_created`
    , `gmt_modified`
    , `topping_type_code`
    , `topping_type_name`
    , `state`
    , `tenant_code`
    , `comment`
    , `extra_info`
) VALUES (
    now()
    , now()
    , #{toppingTypeCode}
    , #{toppingTypeName}
    , #{state}
    , #{tenantCode}
    , #{comment}
    , #{extraInfo, jdbcType=VARCHAR, typeHandler=com.langtuo.teamachine.dao.typehandler.MapStringTypeHandler}
)

mapper.update:
UPDATE tea_topping_type SET
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_type_code = #{toppingTypeCode}

mapper.delete:
DELETE FROM tea_topping_type
WHERE 1 = 1
    and tenant_code = #{tenantCode}
    and topping_type_code = #{toppingTypeCode}
